mapper.select:
SELECT tk_ticket_execution_item.*
FROM tk_ticket_execution_item
WHERE 1=1

     AND tk_ticket_execution_item.id = #{id}


     AND tk_ticket_execution_item.ticket_id = #{ticketId}


     AND tk_ticket_execution_item.equipment_id = #{equipmentId}


     AND tk_ticket_execution_item.equipment_number LIKE CONCAT('%',#{equipmentNumber},'%')


     AND tk_ticket_execution_item.equipment_code LIKE CONCAT('%',#{equipmentCode},'%')


     AND tk_ticket_execution_item.equipment_name LIKE CONCAT('%',#{equipmentName},'%')


     AND tk_ticket_execution_item.equipment_unit LIKE CONCAT('%',#{equipmentUnit},'%')


     AND tk_ticket_execution_item.equipment_spec LIKE CONCAT('%',#{equipmentSpec},'%')


     AND tk_ticket_execution_item.warehouse_id = #{warehouseId}


     AND tk_ticket_execution_item.warehouse_name LIKE CONCAT('%',#{warehouseName},'%')


     AND tk_ticket_execution_item.wh_warehouse_area_id = #{whWarehouseAreaId}


     AND tk_ticket_execution_item.wh_warehouse_area_name LIKE CONCAT('%',#{whWarehouseAreaName},'%')


     AND tk_ticket_execution_item.wh_warehouse_slot_id = #{whWarehouseSlotId}


     AND tk_ticket_execution_item.wh_warehouse_slot_name LIKE CONCAT('%',#{whWarehouseSlotName},'%')
