mapper.select:
SELECT tk_ticket_execution_replace_item.* FROM tk_ticket_execution_replace_item WHERE 1=1
AND tk_ticket_execution_replace_item.id LIKE CONCAT('%',#{ticketExecutionReplaceItem.id},'%')
AND tk_ticket_execution_replace_item.ticket_id = #{ticketExecutionReplaceItem.ticketId}
AND tk_ticket_execution_replace_item.equipment_id = #{ticketExecutionReplaceItem.equipmentId}
AND tk_ticket_execution_replace_item.equipment_number LIKE CONCAT('%',#{ticketExecutionReplaceItem.equipmentNumber},'%')
AND tk_ticket_execution_replace_item.equipment_code LIKE CONCAT('%',#{ticketExecutionReplaceItem.equipmentCode},'%')
AND tk_ticket_execution_replace_item.equipment_name LIKE CONCAT('%',#{ticketExecutionReplaceItem.equipmentName},'%')
AND tk_ticket_execution_replace_item.equipment_unit LIKE CONCAT('%',#{ticketExecutionReplaceItem.equipmentUnit},'%')
AND tk_ticket_execution_replace_item.equipment_spec LIKE CONCAT('%',#{ticketExecutionReplaceItem.equipmentSpec},'%')
AND tk_ticket_execution_replace_item.warehouse_id = #{ticketExecutionReplaceItem.warehouseId}
AND tk_ticket_execution_replace_item.warehouse_name LIKE CONCAT('%',#{ticketExecutionReplaceItem.warehouseName},'%')
AND tk_ticket_execution_replace_item.wh_warehouse_area_id = #{ticketExecutionReplaceItem.whWarehouseAreaId}
AND tk_ticket_execution_replace_item.wh_warehouse_area_name LIKE CONCAT('%',#{ticketExecutionReplaceItem.whWarehouseAreaName},'%')
AND tk_ticket_execution_replace_item.wh_warehouse_slot_id = #{ticketExecutionReplaceItem.whWarehouseSlotId}
AND tk_ticket_execution_replace_item.wh_warehouse_slot_name LIKE CONCAT('%',#{ticketExecutionReplaceItem.whWarehouseSlotName},'%')
